mapper.select:
select diagnosis_data
from ai_model_showcase.transaction_data
where transaction_token = #{transaction_token};

mapper.update:
update set td.diagnosis_data = #{diagnosis_data}
from ai_model_showcase.transaction_data td
where td.transaction_token = #{transaction_token}

update set td.study_data = #{study_data}
from ai_model_showcase.transaction_data td
where td.transaction_token = #{transaction_token}
